import React ,{useState,useEffect}from "react";
import Header from "../../Components/Header/Header";
import Footer from "../../Components/Footer/Footer";
import Content from "../../Components/Content/Content";
import Container from 'react-bootstrap/Container';
import { Navbar, Nav, NavItem, NavDropdown, Button , Form , FormControl,DropdownButton,Dropdown} from 'react-bootstrap';


const Series = ({seriesData}) => {
 
    const [sort, setSort] = useState("title");

    const [searchTerm, setSearchTerm] = useState("");

    const handleChange = event => {
      setSearchTerm(event.target.value);
    };
 


    const compare = (a, b ,c ) => {
      const titleA = a.title.toUpperCase();
      const titleB = b.title.toUpperCase();
      let comparison = 0;

      if (titleA > titleB) {
        comparison = 1;
      } else if (titleA < titleB) {
        comparison = -1;
      }
      return comparison;
    };

    const handleClick = (props) => {
      console.log(props);
      //setSort()
    };

    const year = (a,b) => {
      return  a.releaseYear-b.releaseYear;
    };


    
    return(
    <div>
        <Header/>
       
    <DropdownButton id="dropdown-basic-button" title="Dropdown button">
      <Dropdown.Item  onClick={handleClick}>Action</Dropdown.Item>
      <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
      <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
</DropdownButton>
 
    {/* <Form inline>
      <FormControl type="text" placeholder="Search" className="mr-sm-2"  onChange={handleChange} />
      <Button variant="outline-success">Search</Button>
    </Form>
  </Navbar.Collapse>
  </Navbar> */}

        <Container   style = {{display: 'flex', flexDirection: 'row', maxWidth: '100%', flexWrap: 'wrap'}}> 
          {seriesData.map (  v =>  <Content key={v.title} title = {v.title} url ={v.images.PosterArt.url} /> )} 
        </Container>
        <Footer/>

    </div>);
};

export default Series;
