import React ,{useState,useEffect} from 'react';
import PropTypes from 'prop-types';
import Content from '../../Components/Content/Content';
import Header from "../../Components/Header/Header";
import Footer from "../../Components/Footer/Footer";
import { Dropdown ,Form ,FormControl,Button,Navbar} from 'react-bootstrap';
import Searchbar from '../../Components/Searchbar';
import Dropdownbar from '../../Components/Dropdownbar';
import '../MoviesAndSeries.css';


const Movies = ({moviesData}) => {

  const [sort, setSort] = useState("");
  const [searchData, setSearchData] = useState("");
  const [searchTerm, setSearchTerm] = useState("");


 const myCallback = (searchTerm , sort) => {
  setSearchTerm(searchTerm);
  setSort(sort);
 
};
  
const myCallback2 = ( ee) => {
  setSort(ee);
  console.log(sort);
  console.log(ee);
 
};


  useEffect(() => {
    let results= moviesData.filter(item =>
     item.title.toLowerCase().includes(searchTerm)
     );
     setSearchData(results);
   }, [searchTerm]);

 

   const getSortedData = (sortKey) => { 

    if(sortKey === 'yearAsc') {
       return moviesData.sort(sortYearAsc);
    }
    if(sortKey === 'yearDesc') {
     return moviesData.sort(sortYearDesc);
     }
     if(sortKey === 'titleDesc'){
       return moviesData.sort(sortTitleDesc);
     }
     if(sortKey ==='search'){
       return searchData
     }

    return moviesData.sort(sortTitleAsc);
   };


   const sortTitleAsc = (a, b ) => {
     const titleA = a.title.toUpperCase();
     const titleB = b.title.toUpperCase();
     let comparison = 0;

     if (titleA > titleB) {
       comparison = 1;
     } else if (titleA < titleB) {
       comparison = -1;
     }
     return comparison;
   };

   const sortTitleDesc = (a, b ) => {
     const titleA = a.title.toUpperCase();
     const titleB = b.title.toUpperCase();
     let comparison = 0;

     if (titleA > titleB) {
       comparison = -1;
     } else if (titleA < titleB) {
       comparison = 1;
     }
     return comparison;
   };
   const sortYearAsc = (a,b) => {
       return  a.releaseYear-b.releaseYear;
    
   };

   const sortYearDesc = (a,b) => {
     return  b.releaseYear-a.releaseYear;
  
 };
 
   

return (
  <div>
       <Header/>
       <Navbar  className="navbar"> 
       <Searchbar  callbackFromParent={myCallback}/>
      <Dropdownbar callbackFromParent2={myCallback2}/> 
  
      </Navbar>
        <div   className="content-container"> 
          {getSortedData(sort).map (  v =>  <Content key={v.title} title = {v.title} url ={v.images.PosterArt.url} /> )} 
        </div>
        <Footer/>

        </div>
    
);

}
Movies.propTypes = {
  moviesData: PropTypes.arrayOf(
    PropTypes.shape({
      title: PropTypes.string,
      url: PropTypes.string
    }),
  ).isRequired,

};
export default Movies;
