import React ,{useState,useEffect}from "react";
import Header from "../../Components/Header/Header";
import Footer from "../../Components/Footer/Footer";
import Content from "../../Components/Content/Content";
import Container from 'react-bootstrap/Container';
import { Navbar, Nav, NavItem, NavDropdown, Button , Form , FormControl,DropdownButton,Dropdown} from 'react-bootstrap';


const Series = ({seriesData}) => {
 
    const [sort, setSort] = useState("title");

    const [searchTerm, setSearchTerm] = useState("");

    const handleChange = event => {
      setSearchTerm(event.target.value);
    };
 





    const getSortedData = (sortKey) => { 
     if(sortKey === 'year') {
        return seriesData.sort(sortYear);
     }

     return seriesData.sort(sortTitle);
    };

    const handleSelect = (sortKey) => {
      console.log(sortKey);
      setSort(sortKey);
    };

    const sortTitle = (a, b ) => {
      const titleA = a.title.toUpperCase();
      const titleB = b.title.toUpperCase();
      let comparison = 0;

      if (titleA > titleB) {
        comparison = 1;
      } else if (titleA < titleB) {
        comparison = -1;
      }
      return comparison;
    };

    const sortYear = (a,b) => {
      return  a.releaseYear-b.releaseYear;
    };


    
    return(
    <div>
        <Header/>

        <Dropdown onSelect={handleSelect}>
        <Dropdown.Toggle variant="success" id="dropdown-basic">
          Sort
        </Dropdown.Toggle>

        <Dropdown.Menu>
          <Dropdown.Item  eventKey="title">Title</Dropdown.Item>
          <Dropdown.Item eventKey="releaseYear">Release Year</Dropdown.Item> 
        </Dropdown.Menu>
      </Dropdown>
        
 
    {/* <Form inline>
      <FormControl type="text" placeholder="Search" className="mr-sm-2"  onChange={handleChange} />
      <Button variant="outline-success">Search</Button>
    </Form>
  </Navbar.Collapse>
  </Navbar> */}

        <Container   style = {{display: 'flex', flexDirection: 'row', maxWidth: '100%', flexWrap: 'wrap'}}> 
          {getSortedData(sort).map (  v =>  <Content key={v.title} title = {v.title} url ={v.images.PosterArt.url} /> )} 
        </Container>
        <Footer/>

    </div>);
};

export default Series;
