import React from 'react';
import { useState ,useEffect } from 'react';
import Header from '../Components/Header/Header';
import Footer from '../Components/Footer/Footer';
import Series from '../Series/Series';
import Button from 'react-bootstrap/Button';
import './Homepage.css';

import { BrowserRouter as Router, Route ,Link} from 'react-router-dom';

const HomePage = () => {

  const [data , SetData] = useState([{}]);

  useEffect(() => {
    fetch('http://localhost:3004/movies')
    .then(response => response.json())
      .then((data) => {
        SetData(data);
      
      });
    
  }, []);
  
let seriesData = data.filter(item => 
  item.programType === "series"
);
//console.log(seriesData);

let moviesData = data.filter(item => 
 item.programType === "movie"
);
//console.log(moviesData);

  
  const deneme = () => {
    console.log("tıklama başarılı");
}

return (
  
    <React.Fragment>  
   <Header/>

   <Router> 
   <Link to="/series">About Us </Link>
    <div className = "box">
      <div  class="d-inline-block "> 
   
     <Link to="/series"> <Button  className = "button" style ={{backgroundColor:'black'}}  >  SERIES</Button> </Link>
      <p> Ppopular Series  </p>
      </div>
       <div  class="d-inline-block ">  
        <Button  className = "button"  style ={{backgroundColor:'black'}} onClick={deneme}>MOVIES</Button> 
        <p> Popular Movies</p>
        </div>
        </div>
        </Router>
      <Footer/>
        </React.Fragment>
        
);

}

export default HomePage;
